import React, { useEffect, useState } from 'react';

function App() {
  const [movie, setMovie] = useState(null);

  useEffect(() => {
    fetch('http://localhost:3002/api/movie')
      .then((res) => res.json())
      .then((data) => {
        setMovie(data);
      })
      .catch((err) => console.error('Hata:', err));
  }, []);

  if (!movie) {
    return <div>Yükleniyor...</div>;
  }

  return (
    <div style={{ padding: '20px' }}>
      <h1>Film Rehberi</h1>
      <h2>{movie.title}</h2>
      {movie.poster && movie.poster !== 'Poster yok' && (
        <img
          src={movie.poster}
          alt={movie.title}
          style={{ width: '200px', height: 'auto' }}
        />
      )}
      <p><strong>Oy:</strong> {movie.vote}</p>
      <p>{movie.overview || 'Açıklama yok.'}</p>
    </div>
  );
}

export default App;
